package ascii;

public class AscIITT {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=(int)'a';i<=(int)'z';i++) {
			System.out.print((char)i +"对应的ASCII值==" +i);
		}
		System.out.println("*****");
		for(int i=(int)'A';i<=(int)'Z';i++) {
			System.out.print((char)i +"对应的ASCII值==" +i);
		}

	}

}
